import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Home = () => {
  return (
    <div>
      {/* Hero Section */}
      <section className="bg-gradient-to-br from-primary-50 to-white section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold text-gray-900 mb-6">
              Transformo ideas en
              <span className="text-primary-600"> resultados digitales</span>
            </h1>
            <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
              Servicios freelance que combinan creatividad artística con excelencia técnica. 
              Desarrollo web, branding y soluciones digitales que impulsan tu negocio.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/servicios" className="btn-primary">
                Ver Servicios
              </Link>
              <Link to="/contacto" className="btn-secondary">
                Contactar
              </Link>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Servicios Destacados */}
      <section className="section-padding bg-white">
        <div className="container-max">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">Servicios Principales</h2>
            <p className="text-gray-600 max-w-2xl mx-auto">
              Soluciones completas para hacer crecer tu presencia digital
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                title: 'Desarrollo Web',
                description: 'Sitios web modernos y aplicaciones que convierten visitantes en clientes.',
                icon: '💻'
              },
              {
                title: 'Branding Digital',
                description: 'Identidad visual coherente que refleja la esencia de tu marca.',
                icon: '🎨'
              },
              {
                title: 'Consultoría Tech',
                description: 'Estrategia digital personalizada para optimizar tu presencia online.',
                icon: '🚀'
              }
            ].map((service, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                className="bg-gray-50 p-8 rounded-xl hover:shadow-lg transition-shadow duration-300"
              >
                <div className="text-4xl mb-4">{service.icon}</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-3">{service.title}</h3>
                <p className="text-gray-600">{service.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="section-padding bg-primary-600">
        <div className="container-max text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
          >
            <h2 className="text-3xl font-bold text-white mb-4">
              ¿Listo para llevar tu proyecto al siguiente nivel?
            </h2>
            <p className="text-primary-100 mb-8 max-w-2xl mx-auto">
              Conversemos sobre tu visión y cómo puedo ayudarte a hacerla realidad.
            </p>
            <Link to="/contacto" className="bg-white text-primary-600 hover:bg-gray-100 px-8 py-3 rounded-lg font-medium transition-colors duration-200">
              Empezar Proyecto
            </Link>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
